import React from "react";
import axios from 'axios';
import './RegionAndTemp.css';
import { useEffect, useState } from 'react';

function RegionAndTemp() {

    const [curWeather, setCurWeather] = useState([])
    const api_keys = '43a5e2520a415d989dd6aeb84fccc6c0'
    const getWeather = async () => {
      const url = `http://api.openweathermap.org/data/2.5/weather?q=daegu&appid=${api_keys}`
      return await axios.get(url)
    }

    useEffect(() => {
      getWeather()
      .then((data) => {
        setCurWeather(data)
      })
      .catch(err => console.log(err))
    }, [])

    console.log(curWeather.data)

    return (
        <div>
            <div className="Main-content-RT">
                <span>
                    <div className="Region">
                        Daegu
                    </div>
                    <div className="Condition">
                        It's getting Cold
                    </div>
                </span>
                <span className="Temperature">
                    temp°
                </span>
            </div>
            <div className="Main-content-F">
                <span>
                    <div>습도</div>
                    <div>65%</div>
                </span>
                <span>
                    <div>풍속/풍량</div>
                    <div>1m/s</div>
                </span>
                <span>
                    <div>강수확률</div>
                    <div>30%</div>
                </span>
                <span>
                    <div>미세먼지</div>
                    <div>보통</div>
                </span>
            </div>
        </div>
    )
}

export default RegionAndTemp;
