import './App.css'
import axios from 'axios';
import RegionAndTemp from './components/RegionAndTemp';
import { useEffect, useState } from 'react';


function App() {

  const [curWeather, setCurWeather] = useState('')
  const [isLoading, setIsLoading] = useState(false);
  const api_keys = '43a5e2520a415d989dd6aeb84fccc6c0'
  const getWeather = async () => {
    const url = `http://api.openweathermap.org/data/2.5/weather?q=daegu&appid=${api_keys}`
    return await axios.get(url)
  }

  useEffect(() => {
    setIsLoading(true)
    getWeather()
      .then((data) => {
        setCurWeather(data.data)
      })
      .catch(err => console.log(err))
    setIsLoading(false)
  }, [])

  if (curWeather === '') return <div>로딩 중...</div>
  else {
    console.log(curWeather)
    // const temp = curWeather.
      return(
        <div className='App-background'>
          <header className='App-header'>
            Wooga Weather
          </header>
          <hr />
          <section>
            {isLoading ? <div>로딩 중...</div> : <RegionAndTemp curWeather={curWeather} />}
          </section>
        </div>
      );
  }
}

export default App;
