    // {온도 && 습도} ㅁㄴㅇㄹ : ㅇㄴㅁㄻ
    {temp < 5 ? 추운날 : 더운날}

    return (
        <div>
            <div className="Main-content-RT">
                <span>
                    <div className="Region">
                        {city}
                    </div>
                    <div className="Condition">
                        It's getting Cold
                    </div>
                </span>
                <span className="Temperature">
                    {temp}°
                </span>
            </div>
            <div className="Main-content-F">
                <span>
                    <div>습도</div>
                    <div>{humidity}%</div>
                </span>
                <span>
                    <div>풍속</div>
                    <div>{speed}m/s</div>
                </span>
                <span>
                    <div>미세먼지</div>
                    <div>보통</div>
                </span>
            </div>
        </div>
    )
}

export default RegionAndTemp;
